import axios from 'axios';
import { useKeycloak } from '@react-keycloak/web';

const api = axios.create({
  baseURL: 'http://localhost:3001',
  headers: {
    'Content-Type': 'application/json',
  },
});

// Function to get the Keycloak token
const HttpClient = () => {
  const { keycloak } = useKeycloak(); 
  // Logic to retrieve the token from wherever it's stored in your React app
  return keycloak.token;
};

// Axios interceptor to attach the token to each request
// Axios interceptor to attach the token to each request
api.interceptors.request.use(
  (config) => {
    // const { keycloak } = useKeycloak(); 
    console.log('Interceptor is applied'); 
    const token = HttpClient();
    console.log('Token:', token);
    if (token) {
      config.headers.Authorization = `Bearer ${keycloak.token}`;
    }

    console.log('Request config:', config);

    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

export default api;
